const close=document.getElementById("alert-popup"),back=document.getElementById("tint-background"),displayClicks=document.getElementById("displayclicks"),moreclicks=document.getElementById("moreclicks");var userclicks=0;function showPopup(){close.style.display="block",back.style.display="block",countClicks(),updateClicks(),checkNumberOfClicks()}function closePopup(){close.style.display="none",back.style.display="none"}function countClicks(){userclicks++,saveClicks()}function saveClicks(){localStorage.setItem("userclicks",userclicks)}function updateClicks(){displayClicks.innerHTML=userclicks+" times"}function checkNumberOfClicks(){5<localStorage.getItem("userclicks",userclicks)&&(moreclicks.style.display="block")}function resetClicks(){localStorage.removeItem("userclicks"),userclicks=0,moreclicks.style.display="none",updateClicks()}userclicks=localStorage.getItem("userclicks"),back.addEventListener("click",()=>{closePopup()});