const close=document.getElementById("alert-popup"),back=document.getElementById("tint-background"),displayClicks=document.getElementById("displayclicks");var userclicks=0;function closePopup(){close.style.display="none",back.style.display="none"}function showPopup(){close.style.display="block",back.style.display="block",countClicks(),updateClicks()}function countClicks(){userclicks++,saveClicks()}function saveClicks(){localStorage.setItem("userclicks",userclicks)}function updateClicks(){displayClicks.innerHTML=userclicks+" times"}userclicks=localStorage.getItem("userclicks",userclicks),back.addEventListener("click",()=>{closePopup()});